/**
 * Multibase represents base encoded strings with a prefix first character
 * describing it's encoding.
 */
declare type Multibase<Prefix extends string> = string | (string & {
    [0]: Prefix;
});
declare abstract class MultibaseCodec<Prefix extends string> {
    readonly name: string;
    readonly prefix: Prefix;
    constructor(name: string, prefix: Prefix);
    abstract isValid(str: string): boolean;
    abstract encode(bytes: Uint8Array, prefix?: boolean): string;
    abstract decode(str: string, prefix?: boolean): Uint8Array;
}

export { Multibase, MultibaseCodec };
