declare type Encoder = (bytes: Uint8Array) => Uint8Array | Promise<Uint8Array>;
declare abstract class MultihashHasher<Code extends number = number> {
    readonly name: string;
    readonly code: Code;
    constructor(name: string, code: Code);
    abstract encode(bytes: Uint8Array): Promise<Uint8Array>;
}

export { Encoder, MultihashHasher };
