import { MultihashHasher } from './abstract.mjs';

class GenericHasher extends MultihashHasher {
  constructor(name, code, encoder) {
    super(name, code);
    this._encoder = encoder;
  }
  async encode(bytes) {
    return this._encoder(bytes);
  }
  static from({
    name,
    code,
    encoder
  }) {
    return new GenericHasher(name, code, encoder);
  }
}

export { GenericHasher };
